import {FETCH_DASHBOARD} from '../actions/types'
const initialState ={
    items:[],
    loading:false,
}

export default function dashboardReducer(state=initialState,action){
    switch(action.type){
        case FETCH_DASHBOARD:
           console.log('Reducer') 
          return {
              ...state,
                 item:action.payload,
                 loading:true
          }

        default:
            return state;
    }
}
