import React, { Component } from 'react';
import ReactApexChart from 'react-apexcharts';

class BarChart extends Component {
    constructor(props) {
        super(props);

        this.state = {
            series: [{
                name: 'Total Paid',
                data: [46, 57, 59, 54, 62, 58, 64, 60, 66, 56, 45, 56]
            }, {
                name: 'Total Unpaid',
                data: [74, 83, 102, 97, 86, 106, 93, 114, 94, 68, 78, 88]
            }],
            options : {
                chart: {
                    toolbar: {
                        show: false,
                    }
                },
                plotOptions: {
                    bar: {
                        horizontal: false,
                        columnWidth: '45%',
                        endingShape: 'rounded'	
                    },
                },
                dataLabels: {
                    enabled: false
                },
                legend: {
                  show: false  
                },
                stroke: {
                    show: true,
                    width: 2,
                    colors: ['transparent']
                },
                
                colors: ['#218359', '#FEA086'],
                xaxis: {
                    categories: ["Jan",'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', "Nov", "Dec"],
                },
                grid: {
                    borderColor: '#f1f1f1',
                },
                fill: {
                    opacity: 1
            
                },
                tooltip: {
                    y: {
                        formatter: function (val) {
                            return "$ " + val + " thousands"
                        }
                    }
                }
            }
        };
    }
    render() {
        return (
            <React.Fragment>
                <div id="bar-chart" className="apex-charts">
                <ReactApexChart options={this.state.options} series={this.state.series} type="bar" height="100%" />
                </div>
            </React.Fragment>
        );
    }
}

export default BarChart;
