export const  loginAction=(body)=>async(dispatch,history)=>{
    console.log('LOGIN Action')
        try {    
            const response = await POST_SERVICE(body,LOGIN_API)
            console.log('log res',response)
            console.log('history res',dispatch)
            if(response)
               {           
                dispatch({
                    type:LOGIN,
                    payload:response
                })  
                // history.push("/dashboard")
               }
               
            else
              {
                dispatch({
                    type:LOGIN_FAILED,
                    // payload:response.data
                })  
