import {POST_SERVICE} from "../../services/backend";
import {LOGIN_API} from '../../services/endpoint'
import { LOGIN,LOGIN_FAILED} from '../actions/types'

export const  loginAction=({body,props})=>async(dispatch)=>{

    console.log('LOGIN Action')
        try {    
            const response = await POST_SERVICE(body,LOGIN_API)
            console.log('log res',response)
            console.log('props',props)
            if(response)
               {           
                dispatch({
                    type:LOGIN,
                    payload:response,
                })  
                
               props.history.push("/dashboard")
               }
               
            else
              {
                dispatch({
                    type:LOGIN_FAILED,
                    // payload:response.data
                })  
              }
         
         }
            catch (e) {
              return e.response;
              }
       };
