import {LOGIN} from '../actions/types'
const INIT_STATE ={
    loginResponse:{},
    loading:false,
}

const LoginReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
      case LOGIN:
        console.log('login reducer')
        return {
          ...state,
          loginResponse: action.payload,
        }

      default:
        return state
    }
  }
  
  export default LoginReducer
